import React, { useState, useEffect, useMemo } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [data, setData] = useState(null);
  const [nextPage, setNextPage] = useState(null);
  const [isLoading, setIsLoading] = useState(true);
  const [error, setError] = useState(null);

  useEffect(() => {
    fetchData('http://localhost:8000/candidate_test/fronted/');
  }, []);

  const fetchData = async (url) => {
    setIsLoading(true);
    try {
      const response = await axios.get(url);
      setData(prevData => {
        if (prevData) {
          return {
            ...response.data,
            results: [...prevData.results, ...response.data.results]
          };
        }
        return response.data;
      });
      setNextPage(response.data.next);
    } catch (error) {
      console.error('Error fetching data:', error);
      setError('Failed to fetch data. Please try again later.');
    } finally {
      setIsLoading(false);
    }
  };

  const loadMore = () => {
    if (nextPage) {
      fetchData(nextPage);
    }
  };

  const processData = useMemo(() => {
    if (!data) return {};

    const processedData = {};
    Object.entries(data.results).forEach(([year, months]) => {
      Object.entries(months).forEach(([month, entries]) => {
        entries.forEach(entry => {
          const [dateStr, value] = Object.entries(entry)[0];
          const key = `${year}-${month}`;
          processedData[key] = (processedData[key] || 0) + value;
        });
      });
    });
    return processedData;
  }, [data]);

  const chartData = {
    labels: Object.keys(processData),
    datasets: [
      {
        label: 'Total Value',
        data: Object.values(processData),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Timestamped Values by Month',
      },
    },
  };

  if (isLoading) return <div>Loading data...</div>;
  if (error) return <div>{error}</div>;

  return (
    <div className="App">
      <h1>Timestamped Values</h1>
      {data && (
        <>
          <Bar data={chartData} options={options} />
          {nextPage && <button onClick={loadMore} disabled={isLoading}>
            {isLoading ? 'Loading...' : 'Load More'}
          </button>}
        </>
      )}
    </div>
  );
}

export default App;
