import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { Bar } from 'react-chartjs-2';
import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  BarElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [data, setData] = useState(null);
  const [nextPage, setNextPage] = useState(null);

  useEffect(() => {
    fetchData('http://localhost:8000/candidate_test/fronted/');
  }, []);

  const fetchData = (url) => {
    axios.get(url)
      .then(response => {
        setData(prevData => {
          if (prevData) {
            return {
              ...response.data,
              results: [...prevData.results, ...response.data.results]
            };
          }
          return response.data;
        });
        setNextPage(response.data.next);
      })
      .catch(error => console.error('Error fetching data:', error));
  };

  const loadMore = () => {
    if (nextPage) {
      fetchData(nextPage);
    }
  };

  const processData = (results) => {
    const processedData = [];
    results.forEach(yearObj => {
      const year = Object.keys(yearObj)[0];
      const months = yearObj[year];
      months.forEach(monthObj => {
        const month = Object.keys(monthObj)[0];
        const entries = monthObj[month];
        entries.forEach(entry => {
          const [date, value] = Object.entries(entry)[0];
          processedData.push({ date, value });
        });
      });
    });
    return processedData.sort((a, b) => new Date(a.date) - new Date(b.date));
  };

  const chartData = data ? processData(data.results) : [];

  const chartConfig = {
    labels: chartData.map(item => item.date),
    datasets: [
      {
        label: 'Value',
        data: chartData.map(item => item.value),
        backgroundColor: 'rgba(75, 192, 192, 0.6)',
      },
    ],
  };

  const options = {
    responsive: true,
    plugins: {
      legend: {
        position: 'top',
      },
      title: {
        display: true,
        text: 'Timestamped Values',
      },
    },
    scales: {
      x: {
        type: 'time',
        time: {
          unit: 'day',
          displayFormats: {
            day: 'YYYY-MM-DD'
          }
        },
        title: {
          display: true,
          text: 'Date'
        }
      },
      y: {
        title: {
          display: true,
          text: 'Value'
        }
      }
    }
  };

  return (
    <div className="App">
      <h1>Timestamped Values</h1>
      {data ? (
        <>
          <Bar data={chartConfig} options={options} />
          {nextPage && <button onClick={loadMore}>Load More</button>}
        </>
      ) : (
        <p>Loading data...</p>
      )}
    </div>
  );
}

export default App;
