import React, { useState } from 'react';
import useFetchData from './hooks/useFetchData';
import ChartComponent from './components/ChartComponent';
import Loading from './components/Loading';
import ErrorAlert from './components/ErrorAlert';
import Button from './components/Button';
import Container from './components/Container';
import Title from './components/Title';
import Dropdown from './components/Dropdown';
import Input from './components/Input';

function App() {
  const [selectedYear, setSelectedYear] = useState('');
  const [selectedMonth, setSelectedMonth] = useState('');
  const [pageSize, setPageSize] = useState(10);  // New state for page size

  const { data, years, months, loading, error, fetchData, nextPage } = useFetchData(selectedYear, selectedMonth, pageSize);

  const handleYearChange = (event) => {
    setSelectedYear(event.target.value);
    fetchData(event.target.value, selectedMonth, pageSize);
  };

  const handleMonthChange = (event) => {
    setSelectedMonth(event.target.value);
    fetchData(selectedYear, event.target.value, pageSize);
  };

  const handlePageSizeChange = (event) => {
    setPageSize(Number(event.target.value));
    fetchData(selectedYear, selectedMonth, Number(event.target.value));
  };

  const loadMore = () => {
    if (nextPage) {
      fetchData(selectedYear, selectedMonth, pageSize, nextPage);
    }
  };

  return (
    <Container>
      <div style={{ display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
        <Title text="Timestamped Values Visualization" />
        <div>
          <Dropdown label="Year" options={['', ...years]} value={selectedYear} onChange={handleYearChange} />
          <Dropdown label="Month" options={['', ...months]} value={selectedMonth} onChange={handleMonthChange} />
          <Input label="Entries per page" value={pageSize} onChange={handlePageSizeChange} />
        </div>
      </div>
      {error && <ErrorAlert message={error} />}
      {loading && <Loading />}
      {data && !loading ? (
        <>
          <ChartComponent data={data.results} />
          {nextPage && (
            <Button onClick={loadMore} text="Load More" />
          )}
        </>
      ) : !loading ? (
        <p>No data available</p>
      ) : null}
    </Container>
  );
}

export default App;
